import { useState, useEffect } from "react";
import axios from 'axios';
import React from "react";
import "../components/Header.css";
import aronWhite from "../image/aronWhite.png";
// import logoutIcon from "../image/logoutIcon.png"

// 임시 데이터 (axios 후 삭제 예정)
const User = {
  id: '20231031',
  pw: 'test1031!',
  name: '정혜빈',
  job: '의사'
}

const Header = () => {

  // job, name 상태 업데이트하는 state
  const [job, setJob] = useState('');
  const [name, setName] = useState('');

  // 자동 로그아웃을 위한 state , 초 단위로 60분 설정
  const [time, setTime] = useState(60 * 60);

  // const Timer = () => {
  //   const [min, setMin] = useState('');
  //   const [sec, setSec] = useState('');
  //   const time = useRef()
  // }

  // 컴포넌트 렌더링 될 때마다 특정 작업 수행
  useEffect(()=>{
    const fetchData = async () => {
      try {
        // const response = await axios.get('/api/user(API요청임시주소)');
        // setJob(response.data.job);
        // setName(response.data.name);
        setJob(User.job);
        setName(User.name);
      } catch (error) {
        console.error('데이터 불러오기 실패', error)
      }
    };
    fetchData();
    // 빈 배열 --> 처음 렌더링 될 때만 실행
  },[])

  useEffect(()=>{
    const timerId = setTimeout(()=>{
      // 진행할 함수
    }, 1000);
    return
  })

  return (
    <div className="header-page">
      <div className="header-content">
        <img src={aronWhite} alt="로고" className="header-logo" />
        <ul>
          <li>
            <span>담당{job} [{name}]</span>
          </li>
          <li>
            <span>60:00</span>
          </li>
          <li>
            <span>로그인</span>
          </li>
          {/* <li>
            <img src={logoutIcon}></img>
          </li> */}
        </ul>
      </div>
    </div>
  );
};

export default Header;
