import React, { useState } from 'react'

const Login = () => {

    const [id, setId] = useState("");
    const [pw, setPw] = useState("");
    
    // 유효여부 판단 state
    const [idValid, setIdValid] = useState(false);
    const [pwValid, setPwValid] = useState(false);

    const handleId = (e) => {
        setId(e.target.value);
        // 숫자 8자리의 아이디만 유효한 값으로 설정 (정규표현식)
        const regex = /^\d{8}$/;
        if(regex.test(id)){
            setIdValid(true)
        }
    }

    return (
    <div className='page'>

        <div className="logoWrap">
            <img src="./image/aron.png" alt="로고자리" />
        </div>

        <div className="contentWrap">
            <div className="inputWrap">
                <input type="text" className='input id' placeholder='ID' value={id} onChange={handleId}/>
            </div>
            <div className="errorMessageWrap">
                아이디를 확인해주세요
            </div>
            <div className="inputWrap">
                <input type="text" className='input pw' placeholder='PASSWORD' value={pw}/>
            </div>
            <div className="errorMessageWrap">
                영문,숫자,특수문자 포함 8자리 이상 입력해주세요
            </div>
            <div>
                <button className='bottomButton' disabled={true}>LOGIN</button> 
            </div>
        </div>


    </div>
    )
}

export default Login
