import React, { useEffect, useState } from 'react'

// 아이디 비밀번호 입력에 따른 경고창을 위한 테스트 데이터 (추후 삭제 예정)
const User = {
    id: '20231031',
    pw: 'test1031!'
}

const Login = () => {

    const [id, setId] = useState("");
    const [pw, setPw] = useState("");
    
    // 유효여부 판단 state
    const [idValid, setIdValid] = useState(false);
    const [pwValid, setPwValid] = useState(false);
    // 허용하지 않는 상태 (버튼 비활성화 상태)
    const [notAllow, setNotAllow] = useState(true);

    const handleId = (e) => {
        // onchange 인터페이스 호출시마다 handleId가 실행되는거니까 e.target.value 사용
        const newId = e.target.value;
        setId(newId);
        // 숫자 8자리의 아이디만 유효한 값으로 설정 (정규표현식)
        const idRegex = /^\d{8}$/;
        // 유효하면 true로 아니면 flase로 변경
        if(idRegex.test(newId)){
            setIdValid(true);
        }else{
            setIdValid(false);
        }
    }

    const handlePw = (e) => {
        const newPw = e.target.value;
        setPw(newPw);

        //영어, 숫자, 특수문자 포함 8자 이상 입력해야 유효한 값으로 설정 (정규표현식)
        const pwRegex = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,}$/;
        if(pwRegex.test(newPw)){
            setPwValid(true);
        }else{
            setPwValid(false);
        }
    }

    // idValid와 pwValid가 유효하면 로그인 버튼 활성화 시킴
    useEffect(() => {
        if(idValid && pwValid) {
            setNotAllow(false);
            return;
        } setNotAllow(true);
    }, [idValid, pwValid]);

    return (
    <div className='page'>

        <div className="logoWrap">
            <img src="./image/aron.png" alt="로고자리" />
        </div>

        <div className="contentWrap">
            <div className="inputWrap">
                <input type="text" className='input id' placeholder='ID' value={id} onChange={handleId}/>
            </div>
            <div className="errorMessageWrap">
                {!idValid && id.length > 0 && (
                    <div>아이디를 확인해주세요</div>
                )}
            </div>
            <div className="inputWrap">
                <input type="text" className='input pw' placeholder='PASSWORD' value={pw} onChange={handlePw}/>
            </div>
            <div className="errorMessageWrap">
                {!pwValid && pw.length > 0 && (
                    <div>영문,숫자,특수문자 포함 8자리 이상 입력해주세요</div>
                )}
            </div>
            <div>
                {/* disabled 속성 : 해당 요소가 비활성화 됨을 명시 */}
                <button onClick={onClickconfirmButton} className='bottomButton' disabled={notAllow}>LOGIN</button> 
            </div>
        </div>


    </div>
    )
}

export default Login
